import React from 'react'

class CategoryFilters extends React.Component {

    getCategories = () => {
        return this.props.categories.map(category => {
            return <button className={category===this.props.selectedCategory ? 'selected':undefined} onClick={category =>this.props.setCategory(category)}>{category}</button>
        })
    }

    render() {
        return (
            <div className="category">
                <h5>Category filters</h5>
                {this.getCategories()}
            </div>
        )
    }
}

export default CategoryFilters
