import React from 'react'

class Task extends React.Component {

    state = {
        editing: false
    }

    changeState = () => {
        this.setState({
            editing: !!this.state.editing
        })
    }

    render() {
        return (
            <div className="task">
                <div className="label">{this.props.task.category}</div>
                {
                    this.state.editing ? 
                    <input type="text" value={this.props.task.text} />
                    :
                    <div className="text">{this.props.task.text}</div>
                }
                {
                    this.props.showStretchFeatures? 
                    <React.Fragment>
                        <button onClick={() =>this.props.deleteTask(this.props.task)} className='delete'>X</button>
                        <button onClick={this.changeState}>EDIT</button>
                    </React.Fragment>
                    :
                    null
                }
            </div>
        )
    }
}

export default Task
