import React from 'react'
import MyTasks from './MyTasks';
import NavBar from './components/NavBar'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import SignUp from './components/SignUp'
import Info from './components/Info'
import User from './components/User'
import LogIn from './components/LogIn'

class App extends React.Component {

    state = {
        user: false
    }

    // componentDidMount() {
    //     const token = localStorage.getItem("token")
    //     if (token) {
    //         fetch("http://localhost:3000/api/v1/profile", {
    //             method: 'GET',
    //             headers: {
    //                 Authorization: `Bearer ${token}`
    //             }
    //         })
    //     }
    //     else {
    //         this.props.history.push("/signup")
    //     }
    // }

    signupHandler = (userObj) => {
        let obj={
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'accept': 'application/json'
            },
            body: JSON.stringify({
                user: userObj 
            })
        }
        fetch('http://localhost:3000/api/v1/users', obj)
        .then(response => response.json())
        .then(data => {
            this.setState({
                user: data 
            })
        })
    }

    loginHandler = (userObj) => {

        let obj = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'accept': 'application/json'
            },
            body: JSON.stringify({
                user: userObj
            })
        }

        fetch('http://localhost:3000/api/v1/login', obj)
        .then(response => response.json())
        .then(data => {
            console.log(data.jwt)
            localStorage.setItem('token', data.jwt)
            this.setState({
                user: data.user
            }, () => this.props.history.go('/info'))  //WHY I CANT REDIRECT SUKA EBANNAYA???!!!XYU B O4KO
        })
    }

    render() {
        return (
            <Router>
                <div>
                    <NavBar />
                    <Route exact path='/' component={MyTasks} />
                    <Route exact path='/signup' render={() => <SignUp submitHandler={this.signupHandler}/>} />
                    <Route exact path='/login' render={() => <LogIn submitHandler={this.loginHandler}/>} />
                    <Route exact path='/info' render={() => <Info user={this.state.user}/>} /> 
                    <Route exact path='/user' component={User} />
                </div>
            </Router>
        )
    }
}

export default withRouter(App)
