import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TaskList from './components/TaskList'
import CategoryFilters from './components/CategoryFilters'

class App extends React.Component {

  state = {
    selectedCategory: 'All',
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  setCategory = (selectedCategory) => {
    this.setState({
      selectedCategory: selectedCategory
    })
    this.filterArray()
  }

  filterArray = () => {
    const filtered = this.state.tasks.filter(task => {
      return task.category === this.state.selectedCategory 
    })
    console.log(filtered)
    this.setState({
      tasks: filtered
    })
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <CategoryFilters selectedCategory={this.state.selectedCategory} setCategory={this.setCategory} categories={CATEGORIES}/>
        <div className="tasks">
          <h5>Tasks</h5>
          <TaskList tasks={this.state.tasks}/>
        </div>
      </div>
    );
  }
}


export default App;
